  CREATE OR REPLACE EDITIONABLE FUNCTION "DI"."F_ALTER_CREDENTIAL" 
(
  CRDID IN VARCHAR2
, DBNAMES IN VARCHAR2
, DBHOSTS IN VARCHAR2
, PASSWORDS IN VARCHAR2
) RETURN NUMBER AS 
RESULTS NUMBER;
BEGIN 
      BEGIN
        SELECT DID INTO RESULTS FROM T_DATABASE_CERTIFICATION WHERE CID=CRDID;
